functions.formula:
//Create step-interpolated signal to keep from capturing the linear interpolation between sample points 
 $signalStep = $signal.toStep() 
 //create capsules for every 9 samples ($toCapsulesbyCount) and for every sample ($toCapsules) 
 $toCapsulesbyCount = $signalStep.toCapsulesByCount(9,9*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 5 samples 
 $toCapsules = $signalStep.toCapsules() 
 //Create condition for when the signal is either greater than or less than the mean 
//separate upper and lower to capture when the rule violations occur on the same side of the centerline 
 $condLess = $signalStep.isLessThan($mean) 
 $condGreater = $signalStep.isGreaterThan($mean)  
 //Find when the last 9 samples are fuly within the greater than or less than the mean 
 //use merge to combine overlapping capsules and remove properties 
 $toCapsules.touches(combinewith($toCapsulesbyCount.inside($condLess), $toCapsulesbyCount.inside($condGreater))).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 6 samples ($toCapsulesbyCount) and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(6,6*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 5 samples
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is increasing or decreasing
//separate increasing and decreasing to capture when the rule violations occur in each direction
$condIncreasing = ($signalStep.runningDelta() > 0)
$condDecreasing = ($signalStep.runningdelta() < 0)

//within 6 data points ($toCapsulesByCount), count how many sample points ($toCapsules) are increasing or decreasing
$countIncreasing = $signal.todiscrete().remove($condIncreasing.inverse()).aggregate(count(),$toCapsulesbyCount.move(1ns,0), durationkey())
$countDecreasing = $signal.todiscrete().remove($condDecreasing.inverse()).aggregate(count(),$toCapsulesbyCount.move(1ns,0), durationkey())

//Find when 6 out of 6 are increasing or decreasing
//by setting the count as a property on $toCapsulesByCount and keeping only capsules greater than or equal to 6
$RR3Increasing = $toCapsulesbyCount.setProperty('Run Rule 3 Violations', $countIncreasing, endvalue())
.keep('Run Rule 3 Violations', isGreaterThanorEqualto(5))
$RR3Decreasing = $toCapsulesbyCount.setProperty('Run Rule 3 Violations', $countDecreasing, endvalue())
.keep('Run Rule 3 Violations', isGreaterThanorEqualto(5))

//Find every sample point capsule ($toCapsules) that touches a run rule violation capsule
//Combine increasing and decreasing into one condition and use merge to combine overlapping capsules and to remove properties
$toCapsules.touches($RR3Increasing or $RR3Decreasing).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 14 samples ($toCapsulesbyCount), every 2 samples ($EveryTwoSamples), and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(14, 14*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 14 samples
$EveryTwoSamples = $signalStep.toCapsulesByCount(2, 2*$maxinterp)
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is increasing or decreasing
//separate increasing and decreasing to capture when the rule violations occur in each direction
$delta = $signalStep.runningdelta(startkey())
$condIncreasing = ($delta >= 0)
$condDecreasing = ($delta <= 0)

//Create a condition for times when every other sample does not alternate between increasing and decreasing
$condNotAlternating = combinewith($EveryTwoSamples.inside($condDecreasing.move(1ns).merge(true)), $EveryTwoSamples.inside($condIncreasing.move(1ns).merge(true)))

//Remove the data points when the trend of the data on both sides of that data point is not alternating direction
$countNotAlternating = $signal.todiscrete().remove($condNotAlternating.afterstart(1ns)).aggregate(count(),$toCapsulesbyCount.shrink(1ns), durationkey()).move(1ns)

//Find when the 12 internal samples (excluding the first and last data point in the last 14 samples) are all alternating directions
//Use merge to combine overlapping capsules and to remove properties
$toCapsules.touches($toCapsulesbyCount.setProperty('Run Rule 4 Violations', $countNotAlternating, endvalue())
.keep('Run Rule 4 Violations', isGreaterThanorEqualto(12))).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 3 samples ($toCapsulesbyCount) and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(3,3*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 3 samples
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is not between +/-2 sigma limits
//separate upper and lower to capture when the rule violations occur on the same side of the centerline
$condLess = ($signalStep <= $minus2sd)
$condGreater = ($signalStep >= $plus2sd)

//within 3 data points ($toCapsulesByCount), count how many sample points are not between +/-2 sigma limits
$countLess = $signal.todiscrete().remove(not $condLess).aggregate(count(),$toCapsulesbyCount,durationKey())
$countGreater = $signal.todiscrete().remove(not $condGreater).aggregate(count(),$toCapsulesbyCount,durationKey())

//Find when 2+ out of 3 are outside of +/-2 sigma limits 
//by setting the count as a property on $toCapsulesByCount and keeping only capsules greater than or equal to 2
$RR5below = $toCapsulesbyCount.setProperty('Run Rule 5 Violations', $countLess, endvalue())
.keep('Run Rule 5 Violations', isGreaterThanOrEqualto(2))
$RR5above = $toCapsulesbyCount.setProperty('Run Rule 5 Violations', $countGreater, endvalue())
.keep('Run Rule 5 Violations', isGreaterThanOrEqualto(2))

//Find every sample point capsule that touches a run rule violation capsule
//Combine upper and lower into one condition and use merge to combine overlapping capsules and to remove properties
$toCapsules.touches($RR5below or $RR5above).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 5 samples ($toCapsulesbyCount) and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(5,5*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 5 samples
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is not between +/-1 sigma limits
//separate upper and lower to capture when the rule violations occur on the same side of the centerline
$condLess = ($signalStep <= $minus1sd)
$condGreater = ($signalStep >= $plus1sd)

//within 5 data points ($toCapsulesByCount), count how many sample points ($toCapsules) are not between +/-1 sigma limits
$countLess = $signal.toDiscrete().remove(not $condLess).aggregate(count(),$toCapsulesbyCount, durationkey())
$countGreater = $signal.toDiscrete().remove(not $condGreater).aggregate(count(),$toCapsulesbyCount,durationkey())

//Find when 4+ out of 5 are outside of +/-1 sigma limits
//by setting the count as a property on $toCapsulesByCount and keeping only capsules greater than or equal to 4
$RR6below = $toCapsulesbyCount.setProperty('Run Rule 6 Violations', $countLess, endvalue())
.keep('Run Rule 6 Violations', isGreaterThanOrEqualto(4))
$RR6above = $toCapsulesbyCount.setProperty('Run Rule 6 Violations', $countGreater, endvalue())
.keep('Run Rule 6 Violations', isGreaterThanOrEqualto(4))

//Find every sample point capsule ($toCapsules) that touches a run rule violation capsule
//Combine upper and lower into one condition and use merge to combine overlapping capsules and to remove properties
$toCapsules.touches($RR6below or $RR6above).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 15 samples ($toCapsulesbyCount) and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(15,15*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 15 samples
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is between +/-1 sigma limits
$condWithin = ($signalStep >= $minus1sd and $signalStep <= $plus1sd)

//within 15 data points ($toCapsulesByCount), count how many sample points ($toCapsules) are between +/-1 sigma limits
$countWithin = $signal.toDiscrete().remove(not $condWithin).aggregate(count(),$toCapsulesbyCount, durationkey())

//Find when 15 out of 15 are within +/-1 sigma limits
//by setting the count as a property on $toCapsulesByCount and keeping only capsules equal to 15
$RR7 = $toCapsulesbyCount.setProperty('Run Rule 7 Violations', $countWithin, endvalue())
.keep('Run Rule 7 Violations', isEqualto(15))

//Find every sample point capsule ($toCapsules) that touches a run rule violation capsule
//Use merge to combine overlapping capsules and to remove properties
$toCapsules.touches($RR7).merge(true)

//Create step-interpolated signal to keep from capturing the linear interpolation between sample points
$signalStep = $signal.toStep()

//create capsules for every 8 samples ($toCapsulesbyCount) and for every sample ($toCapsules)
$toCapsulesbyCount = $signalStep.toCapsulesByCount(8,8*$maxinterp) //set the maximum duration based on the longest time you would expect to collect 8 samples
$toCapsules = $signalStep.toCapsules()

//Create condition for when the signal is not between +/-1 sigma limits
//separate upper and lower and combined to capture when the rule violations occur on the same side of the centerline
$condLess = ($signalStep <= $minus1sd)
$condGreater = ($signalStep >= $plus1sd)
$condEither = (($signalStep <= $minus1sd) or ($signalStep >= $plus1sd))

//within 8 data points ($toCapsulesByCount), count how many sample points are not between +/-1 sigma limits
$countEither = $signal.toDiscrete().remove(not $condEither).aggregate(count(),$toCapsulesbyCount,durationkey())

//Find when 8 consecutive points are outside of +/-1 sigma limits, with points on each side of the mean
//by setting the count as a property on $toCapsulesByCount and keeping only capsules greater than or equal to 4
$RR8 = $toCapsulesbyCount.setProperty('Run Rule 8 Violations', $countEither, endvalue())
.keep('Run Rule 8 Violations', isGreaterThanOrEqualto(8)).touches($condLess).touches($condGreater)

//Find every sample point capsule ($toCapsules) that touches a run rule violation capsule
//Combine upper and lower into one condition, use merge to combine overlapping capsules and remove properties, and set property for run rule
$toCapsules.touches($RR8).merge(true)
